import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [model, setModel] = useState('model_1'); // Default model
  const [text, setText] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  // const analyzeText = async (e) => {
  //   e.preventDefault();
  //   setLoading(true); // Start loading

  //   try {
  //     // Send data to the backend using Axios
  //     const response = await axios.post(`http://127.0.0.1:8000/result/${model}`, {
  //       text: text
  //     });

  //     // Update the result with backend response
  //     setResult(response.data.analysis); // Assuming 'analysis' is returned from the backend
  //   } catch (error) {
  //     setResult('An error occurred: ' + error.message); // Handle error
  //   } finally {
  //     setLoading(false); // Stop loading
  //   }
  // };

  const analyzeText = async (e) => {
    e.preventDefault();

    if (text.trim() === "") {
      setResult("Please enter the text properly.");
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post(`http://127.0.0.1:8000/result/${model}`, {
        text,
      });
      setResult(response.data.analysis); // Assuming 'analysis' is returned from the backend
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setResult(error.response.data.detail || "Please enter the text properly.");
      } else {
        setResult("An unexpected error occurred. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className="container">
      <h1 className="heading">Text Analysis</h1>
      <form onSubmit={analyzeText} className="form">
        {/* Model selection dropdown */}
        <label className="label">
          Choose Model:
          <select
            value={model}
            onChange={(e) => setModel(e.target.value)}
            className="model"
          >
            <option value="model_1">Roberta Base Go Emotions</option>
            <option value="model_2">Bert Base Multilingual Sentiment</option>
          </select>
        </label>

        {/* Text input */}
        <label className="label">
          Enter text:
          <input
            type="text"
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="textInput"
            placeholder="Enter text to analyze..."
          />
        </label>

        {/* Submit Button */}
        <button type="submit" disabled={loading} className="button">
          {loading ? 'Analyzing...' : 'Analyze Text'}
        </button>
      </form>

      {/* Result Display */}
      {result && (
        <div className="result">
          <h2>Result:</h2>
          <p>Label: {result.label}</p>
          <p>Score: {result.score}</p>
        </div>
      )}
    </div>
  );
}

export default App;
