import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import ChartComponent from './components/ChartComponent';
function App() {
  const [model, setModel] = useState('model_1'); // Default model
  const [text, setText] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/history');
      setHistory(response.data.history);
    } catch (error) {
      console.error("Error fetching history:", error);
    }
  };

  const analyzeText = async (e) => {
    e.preventDefault();

    if (text.trim() === "") {
      setResult("Please enter the text properly.");
      return;
    }

    setLoading(true);
    setResult(null); // Clear previous result during loading

    try {
      const response = await axios.post(`http://127.0.0.1:8000/result/${model}`, {
        text,
      });
      setResult(response.data.analysis); // Assuming 'analysis' is returned from the backend
      fetchHistory(); // Refresh history after new analysis
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setResult(error.response.data.detail || "Please enter the text properly.");
      } else {
        setResult("An unexpected error occurred. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  const formatChartData = (modelId) => {
    const modelHistory = history.filter(entry => entry.model_id === modelId);
    const labels = modelHistory.map(entry => entry.analysis.label);
    const scores = modelHistory.map(entry => entry.analysis.score);

    return {
      labels: labels,
      datasets: [
        {
          label: 'Scores',
          data: scores,
          borderColor: modelId === 'model_1' ? 'rgba(75,192,192,1)' : 'rgba(153,102,255,1)',
          fill: false,
        },
      ],
    };
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Model Analysis',
      },
    },
  };

  return (
    <div className="container">
      <h1 className="heading">Text Analysis</h1>
      <form onSubmit={analyzeText} className="form">
        {/* Model selection dropdown */}
        <label className="label">
          Choose Model:
          <select
            value={model}
            onChange={(e) => setModel(e.target.value)}
            className="model"
          >
            <option value="model_1">Roberta Base Go Emotions</option>
            <option value="model_2">Bert Base Multilingual Sentiment</option>
          </select>
        </label>

        {/* Text input */}
        <label className="label">
          Enter text:
          <input
            type="text"
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="textInput"
            placeholder="Enter text to analyze..."
          />
        </label>

        {/* Submit Button */}
        <button type="submit" disabled={loading} className="button">
          {loading ? 'Analyzing...' : 'Analyze Text'}
        </button>
      </form>

      {/* Result Display */}
      {loading ? (
        <div className="loading">Loading, please wait...</div>
      ) : result && (
        <div className="result">
          {result.label ? (
            <>
              <h2>Result:</h2>
              <p>Label: {result.label}</p>
              <p>Score: {result.score}</p>
            </>
          ) : (
            <p style={{ color: "red" }}>{result}</p> // Show errors in red
          )}
        </div>
      )}

      {/* Charts */}
      <h2>Roberta Base Go Emotion(model_1) - analysis</h2>
      <ChartComponent chartData={formatChartData('model_1')} chartOptions={chartOptions} />

      <h2>Bert Base Multilingual Sentiment(model_2) - analysis</h2>
      <ChartComponent chartData={formatChartData('model_2')} chartOptions={chartOptions} />
    </div>
  );
}

export default App;
