import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import './App.css';

function App() {
  const [model, setModel] = useState('model_1');
  const [text, setText] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [history, setHistory] = useState([]);
  const [chartData, setChartData] = useState(null);

  // Fetch history on component mount
  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/history');
      setHistory(response.data.history);
      updateChartData(response.data.history);
    } catch (error) {
      console.error("Failed to fetch history:", error);
    }
  };

  const updateChartData = (data) => {
    const labels = data.map((_, index) => `Entry ${index + 1}`);
    const scores = data.map((item) => item.analysis.score);

    setChartData({
      labels,
      datasets: [
        {
          label: 'Scores',
          data: scores,
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 2,
        },
      ],
    });
  };

  const analyzeText = async (e) => {
    e.preventDefault();

    if (text.trim() === "") {
      setResult("Please enter the text properly.");
      return;
    }

    setLoading(true);
    setResult(null);

    try {
      const response = await axios.post(`http://127.0.0.1:8000/result/${model}`, { text });
      setResult(response.data.analysis);
      fetchHistory(); // Refresh history after new analysis
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setResult(error.response.data.detail || "Please enter the text properly.");
      } else {
        setResult("An unexpected error occurred. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1 className="heading">Text Analysis</h1>
      <form onSubmit={analyzeText} className="form">
        <label className="label">
          Choose Model:
          <select
            value={model}
            onChange={(e) => setModel(e.target.value)}
            className="model"
          >
            <option value="model_1">Roberta Base Go Emotions</option>
            <option value="model_2">Bert Base Multilingual Sentiment</option>
          </select>
        </label>

        <label className="label">
          Enter text:
          <input
            type="text"
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="textInput"
            placeholder="Enter text to analyze..."
          />
        </label>

        <button type="submit" disabled={loading} className="button">
          {loading ? 'Analyzing...' : 'Analyze Text'}
        </button>
      </form>

      {loading ? (
        <div className="loading">Loading, please wait...</div>
      ) : result && (
        <div className="result">
          {result.label ? (
            <>
              <h2>Result:</h2>
              <p>Label: {result.label}</p>
              <p>Score: {result.score}</p>
            </>
          ) : (
            <p style={{ color: "red" }}>{result}</p>
          )}
        </div>
      )}

      {/* History Table */}
      {history.length > 0 && (
        <div className="history">
          <h2>Analysis History</h2>
          <table>
            <thead>
              <tr>
                <th>#</th>
                <th>Text</th>
                <th>Model</th>
                <th>Label</th>
                <th>Score</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {history.map((entry, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{entry.text}</td>
                  <td>{entry.model_id}</td>
                  <td>{entry.analysis.label}</td>
                  <td>{entry.analysis.score.toFixed(2)}</td>
                  <td>{new Date(entry.timestamp).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Graph */}
      {chartData && (
        <div className="chart">
          <h2>Score Trends</h2>
          <Line data={chartData} />
        </div>
      )}
    </div>
  );
}

export default App;
